import React, { useState, useEffect } from 'react';
import { TextField, Box, Button, Typography, Paper } from '@material-ui/core';
import FireBase from 'react-file-base64';
import { useDispatch, useSelector } from 'react-redux';
import DoneOutlineIcon from '@mui/icons-material/DoneOutline';
import useStyles from './styles';
import { createMeal,updateMeal } from '../../actions/menu';
import  Axios  from 'axios';

// GET THE CURRENT ID

const curTableIDs = [
  {
    value: 1,
    label: '1',
  },
  {
    value: 2,
    label: '2',
  },
  {
    value: 3,
    label: '3',
  },
  {
    value: 4,
    label: '4',
  },
  {
    value: 5,
    label: '5',
  },
  {
    value: 6,
    label: '6',
  },
];

const curMealIDs = [
    {
      value: 1,
      label: '1',
    },
    {
      value: 2,
      label: '2',
    },
    {
      value: 3,
      label: '3',
    },
    {
      value: 4,
      label: '4',
    },
  ];

const curMeals = [
    {
      value: 'Pasta',
      label: 'Pasta',
    },
    {
      value: 'Pizza',
      label: 'Pizza',
    },
    {
      value: 'Burger',
      label: 'Burger',
    },
    {
      value: 'Salad',
      label: 'Salad',
    },
  ];

const TableCreate = () => {
    // const [curMeal, setCurMeal] = React.useState('EUR');
    const [meal_id, setMealID] = React.useState(0);
    const [meal_name, setMealName] = React.useState("");
    const [table_id, setTableID] = React.useState(0);
    const [price, setPrice] = React.useState(0);
    const [quantity, setQuantity] = React.useState(0);

    const [order_info, setOrderInfo] = React.useState([]);

    // const handleChange = (event) => {
    //     setCurMeal(event.target.value);
    // };
    const addTable = (meal_id, meal_name, table_id, price, quantity) => {
    // const addTable = () => {
      Axios.post('http://localhost:3002/create', {
        // meal_id: meal_id,
        // meal_name: meal_name,
        // table_id: table_id,
        // price: price,
        // quantity: quantity,
        params: {
          meal_id: meal_id,
          meal_name: meal_name,
          table_id: table_id,
          price: price,
          quantity: quantity,
        }
      }).then((response) => {
        setOrderInfo({
          order_info,
          [meal_id]: meal_id,
          [meal_name]: meal_name,
          [table_id]: table_id,
          [price]: price,
          [quantity]: quantity,
          },
        );
        // console.log('error here');
        // setOrderInfo([
        //   ...order_info,
        //   {
        //     meal_id: meal_id,
        //     meal_name: meal_name,
        //     table_id: table_id,
        //     price: price,
        //     quantity: quantity,
        //   },
        // ]);
      });
    };

    return (
        <Box component="form" 
             sx={{ '& .MuiTextField-root': { m: 1, width: '25ch' },}}
             noValidate autoComplete="off">
            <Paper>
                Create Table
            <div>
                <TextField
                    id="filled-select-currency-native"
                    select
                    label="Meal ID"
                    // value={curMealIDs}
                    onChange={(event) => {
                      setMealID(event.target.value);
                    }}
                    SelectProps={{
                        native: true,
                    }}
                    // helperText="Please select meal id"
                    variant="filled"
                    >
                    {curMealIDs.map((option) => (
                        <option key={option.value} value={option.value}>
                        {option.label}
                        </option>
                    ))}
                </TextField>
                <TextField
                    id="filled-select-currency-native"
                    select
                    label="Meal Name"
                    // value={curMeal}
                    onChange={(event) => {
                      setMealName(event.target.value);
                    }}
                    SelectProps={{
                        native: true,
                    }}
                    // helperText="Please select meal"
                    variant="filled"
                    >
                    {curMeals.map((option) => (
                        <option key={option.value} value={option.value}>
                        {option.label}
                        </option>
                    ))}
                </TextField>
                <TextField
                    id="filled-select-currency-native"
                    select
                    label="Table ID"
                    // value={curTableIDs}
                    onChange={(event) => {
                      setTableID(event.target.value);
                    }}
                    SelectProps={{
                        native: true,
                    }}
                    // helperText="Please select meal id"
                    variant="filled"
                    >
                    {curTableIDs.map((option) => (
                        <option key={option.value} value={option.value}>
                        {option.label}
                        </option>
                    ))}
                </TextField>
                <TextField
                    id="standard-number"
                    label="Quantity"
                    type="number"
                    InputLabelProps={{
                        shrink: true,
                    }}
                    variant="standard"
                    margin='dense'
                    onChange={(event) => {
                      setQuantity(event.target.value);
                    }}
                />
                <TextField
                    id="standard-number"
                    label="Price"
                    type="number"
                    InputLabelProps={{
                        shrink: true,
                    }}
                    variant="standard"
                    margin='normal'
                    onChange={(event) => {
                      setPrice(event.target.value);
                    }}
                />
                <Button variant="outlined" size="medium" startIcon={<DoneOutlineIcon />} onClick={addTable}>
                    Done
                </Button>
            </div>
            </Paper>
        </Box>
    );
}

export default TableCreate;
