import React from 'react';
import { Card, CardActions, CardContent, CardMedia, Button, Typography } from '@material-ui/core';
// import ThumbUpAltIcon from '@material-ui/icons/ThumbUpAlt';
import AddIcon from '@mui/icons-material/Add';
import RemoveIcon from '@mui/icons-material/Remove';
import DeleteIcon from '@material-ui/icons/Delete';
import MoreHorizIcon from '@material-ui/icons/MoreHoriz';
import moment from 'moment';
import { useDispatch } from 'react-redux';

import { deleteMeal, addMeal, removeMeal } from '../../../actions/menu';

import useStyles from './styles';

const Meal = ({ meal, setCurrentId }) => {
    const classes = useStyles();
    const dispatch = useDispatch();
    return (
        <Card className={classes.card}>
            <CardMedia className={classes.media} image={meal.selectedFile} title={meal.title} />
            <div className={classes.overlay}>
                <Typography variant="h6">{meal.creator}</Typography>
                <Typography variant="body2">{moment(meal.createdAt).fromNow()}</Typography>
            </div>
            <div className={classes.overlay2}>
                <Button 
                style={{color: 'white'}} 
                size="small" 
                onClick={() => setCurrentId(meal._id)}>
                    <MoreHorizIcon fontSize="default" />
                </Button>
            </div>
            <div className={classes.details}>
                <Typography>{meal.tags.map((tag) => `#${tag} `)}</Typography>
            </div>
            {/* <Typography className={classes.title} >{meal.title}</Typography> */}
            <Typography className={classes.title} gutterBottom>{meal.title}</Typography>
            <CardContent>
                <Typography variant="body2" color="textSecondary" component="p" >{meal.message}</Typography>
            </CardContent>
            <CardActions className={classes.cardActions}>
                <Button size="small" color="primary" onClick={() => dispatch(addMeal(meal._id))}>
                    {/* <ThumbUpAltIcon fontSize="small"/> */}
                    <AddIcon fontSize="small"/>
                     Add 
                    {/* &nbsp; Add &nbsp; */}
                    {meal.mealCount}
                </Button>
                <Button size="small" color="primary" onClick={() => dispatch(removeMeal(meal._id))}>
                    {/* <ThumbUpAltIcon fontSize="small"/> */}
                    <RemoveIcon fontSize="small"/>
                     Remove
                    {/* {meal.mealCount} */}
                </Button>
                <Button size="small" color="primary" onClick={() => dispatch(deleteMeal(meal._id))}>
                    <DeleteIcon fontSize="small"/>
                    Delete Meal
                </Button>
            </CardActions>
        </Card>
    );
}

export default Meal;
