import React, { useState, useEffect } from 'react';
import { TextField, Box, Button, Typography, Paper, Divider } from '@material-ui/core';
import FireBase from 'react-file-base64';
import { useDispatch, useSelector } from 'react-redux';
import DoneOutlineIcon from '@mui/icons-material/DoneOutline';
import useStyles from './styles';
import { createMeal,updateMeal } from '../../actions/menu';
import { spacing } from '@material-ui/system';
import { updateTable } from '../../actions/tables';

// GET THE CURRENT ID
const curTableIDs = [
    {
      value: 1,
      label: '1',
    },
    {
      value: 2,
      label: '2',
    },
    {
      value: 3,
      label: '3',
    },
    {
      value: 4,
      label: '4',
    },
    {
      value: 5,
      label: '5',
    },
    {
      value: 6,
      label: '6',
    },
  ];

  const curMealIDs = [
      {
        value: 1,
        label: '1',
      },
      {
        value: 2,
        label: '2',
      },
      {
        value: 3,
        label: '3',
      },
      {
        value: 4,
        label: '4',
      },
    ];

  const curMeals = [
      {
        value: 'Pasta',
        label: 'Pasta',
      },
      {
        value: 'Pizza',
        label: 'Pizza',
      },
      {
        value: 'Burger',
        label: 'Burger',
      },
      {
        value: 'Salad',
        label: 'Salad',
      },
    ];


// const TableUpdate = ({ oid }) => {
const TableUpdate = ({ oid }) => {
    const [tableData, setTableData] = useState({ order_id: 1, meal_id: 1, meal_name: 'Pasta', table_id: 1, price: 0, quantity: 0 });
    const dispatchTables = useDispatch();
    const handleSubmit = (e) => {
        e.preventDefault();
        console.log('tableData is ' + tableData.meal_name);
        dispatchTables(updateTable(tableData));
    }
    return (
        <Box component="form" 
             sx={{ '& .MuiTextField-root': { m: 1, width: '25ch' },}}
             noValidate autoComplete="off">
            <Paper>
                Update Table
            <div>
                <TextField
                    id="standard-number"
                    label="Order ID"
                    type="number"
                    InputLabelProps={{
                        shrink: true,
                    }}
                    variant="standard"
                    margin='dense'
                    value={tableData.order_id} 
                    onChange={(e) => setTableData({ ...tableData, order_id: e.target.value })}
                />
                <TextField
                id="standard-read-only-input"
                label="Meal ID"
                defaultValue="Hello World"
                margin='dense'
                // InputProps={{
                //     readOnly: true,
                // }}
                variant="standard"
                />
                <TextField
                id="standard-read-only-input"
                label="Meal Name"
                defaultValue="Hello World"
                margin='dense'
                // InputProps={{
                //     readOnly: true,
                // }}
                variant="standard"
                />
                <TextField
                id="standard-read-only-input"
                label="Table ID"
                defaultValue="Hello World"
                margin='dense'
                // InputProps={{
                //     readOnly: true,
                // }}
                variant="standard"
                />
                <TextField
                id="standard-number"
                label="Quantity"
                type="number"
                margin='none'
                InputLabelProps={{
                    shrink: true,
                }}
                variant="standard"
                />
                <TextField
                id="standard-number"
                label="Price"
                type="number"
                margin='normal'
                InputLabelProps={{
                    shrink: true,
                }}
                variant="standard"
                />
                <Divider></Divider>
                <Button variant="outlined" startIcon={<DoneOutlineIcon />} >
                    Done
                </Button>
            </div>
            </Paper>
        </Box>
    );
}

export default TableUpdate;
