// import React from 'react';
import { Card, CardActions, CardContent, CardMedia, Button, Typography } from '@material-ui/core';
// import ThumbUpAltIcon from '@material-ui/icons/ThumbUpAlt';
import AddIcon from '@mui/icons-material/Add';
import UpdateIcon from '@mui/icons-material/Update';
import RemoveIcon from '@mui/icons-material/Remove';
import DeleteIcon from '@material-ui/icons/Delete';
import CheckIcon from '@mui/icons-material/Check';
import MoreHorizIcon from '@material-ui/icons/MoreHoriz';
import moment from 'moment';
import { useDispatch } from 'react-redux';

import { deleteMeal, addMeal, removeMeal } from '../../../actions/menu';

import useStyles from './styles';

import getTables from '../../../actions/tables';

import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';

import React, { useState, useEffect } from 'react';
import  Axios  from 'axios';

const TableEach = ({ tid }) => {
    const classes = useStyles();
    const dispatch = useDispatch();

    const [TablesList, setTablesList] = useState([]);

    // const getTables = getTables();

    Axios.get('http://localhost:3002', {
        params: {
            tid: tid,
        },
    }).then((response) => {
                setTablesList(response.data);
    });

    return (
        <TableContainer component={Paper}>
            <Table sx={{ minWidth: 650 }} aria-label="simple table" >
                <TableHead>
                <TableRow>
                    <TableCell align="right">order_id</TableCell>
                    <TableCell align="right">meal_id</TableCell>
                    <TableCell align="right">meal_name</TableCell>
                    <TableCell align="right">table_id</TableCell>
                    <TableCell align="right">price</TableCell>
                    <TableCell align="right">amount</TableCell>
                    <TableCell align='right'>
                        <Button size="small" color="primary" >
                            <AddIcon fontSize="small"/>
                                Add 
                        </Button>
                        <Button size="small" color="primary" >
                            <CheckIcon fontSize="small"/>
                                结账
                        </Button>
                    </TableCell>
                </TableRow>
                </TableHead>
                <TableBody>
                {TablesList.map((order) => (
                    <TableRow
                    key={order.id}
                    sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
                    >
                    <TableCell align="right">{order.order_id}</TableCell>
                    <TableCell align="right">{order.meal_id}</TableCell>
                    <TableCell align="right">{order.meal_name}</TableCell>
                    <TableCell align="right">{order.table_id}</TableCell>
                    <TableCell align="right">{order.price}</TableCell>
                    <TableCell align="right">{order.amount}</TableCell>
                    <TableCell align='right'>
                        <Button size="small" color="primary" >
                            <UpdateIcon fontSize="small"/>
                                Update
                        </Button>
                        <Button size="small" color="primary" >
                            <DeleteIcon fontSize="small"/>
                                Delete
                        </Button>
                    </TableCell>
                    </TableRow>
                ))}
                </TableBody>
            </Table>
    </TableContainer>
    );
}

export default TableEach;
