import React from 'react';
import { Card, CardActions, CardContent, CardMedia, Button, Typography } from '@material-ui/core';
import ThumbUpAltIcon from '@material-ui/icons/ThumbUpAlt';
import AddIcon from '@mui/icons-material/Add';
import DeleteIcon from '@material-ui/icons/Delete';
import MoreHorizIcon from '@material-ui/icons/MoreHoriz';
import moment from 'moment';
import { useDispatch } from 'react-redux';

import { deleteMeal, likeMeal } from '../../../actions/menu';

import useStyles from './styles';

const Meal = ({ meal, setCurrentId }) => {
    const classes = useStyles();
    const dispatch = useDispatch();
    return (
        <Card className={classes.card}>
            <CardMedia className={classes.media} image={meal.selectedFile} title={meal.title} />
            <div className={classes.overlay}>
                <Typography variant="h6">{meal.creator}</Typography>
                <Typography variant="body2">{moment(meal.createdAt).fromNow()}</Typography>
            </div>
            <div className={classes.overlay2}>
                <Button 
                style={{color: 'white'}} 
                size="small" 
                onClick={() => setCurrentId(meal._id)}>
                    <MoreHorizIcon fontSize="default" />
                </Button>
            </div>
            <div className={classes.details}>
                <Typography variant="body2" color="textSecondary">{meal.tags.map((tag) => `#${tag} `)}</Typography>
            </div>
            <Typography className={classes.title} variant="h5" gutterBottom>{meal.title}</Typography>
            <CardContent>
                <Typography variant="body2" color="textSecondary" component="p" >{meal.message}</Typography>
            </CardContent>
            <CardActions className={classes.cardActions}>
                <Button size="small" color="primary" onClick={() => dispatch(likeMeal(meal._id))}>
                    {/* <ThumbUpAltIcon fontSize="small"/> */}
                    <AddIcon fontSize="small"/>
                    &nbsp; Like &nbsp;
                    {meal.likeCount}
                </Button>
                <Button size="small" color="primary" onClick={() => dispatch(deleteMeal(meal._id))}>
                    <DeleteIcon fontSize="small"/>
                    Delete
                </Button>
            </CardActions>
        </Card>
    );
}

export default Meal;
