import React, { useState, useEffect } from 'react';
import { Container, AppBar, Typography, Grow, Grid, Divider, LinearProgress, Button } from '@material-ui/core';
import { useDispatch } from 'react-redux'; 

import { getMenu } from './actions/menu';
import { getRevenue } from './actions/revenue';
import Menu from './components/Menu/Menu';
import Form from './components/Form/Form';
import Revenue from './components/Revenue/Revenue';
// import memories from './images/memories.jpeg'
import yancong from './images/yancong.jpeg'
import mcute from './images/mcute.jpeg'
import useStyles from './styles';
// import DividerLine from './DividerLine.js' ;
import "@fontsource/roboto"

import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';
import Box from '@mui/material/Box';

const App = () => {
    const [currentId, setCurrentId] = useState(null);
    const [currentIdRevenue, setCurrentIdRevenue] = useState(null);
    const classes = useStyles();
    const dispatch = useDispatch();
    const dispatchRevenue = useDispatch();

    const [value, setValue] = React.useState(0);

    const handleChange = (event: React.SyntheticEvent, newValue: number) => {
        setValue(newValue);
    };

    useEffect(() => {
        dispatch(getMenu());
    }, [currentId, dispatch]);


    return(
        <Container maxidth="lg">
            <AppBar className={classes.appBar} position="static" color="inherit" style={{ background: 'transparent', boxShadow: 'none'}}>
                <img className={classes.image} src={yancong} alt="yancong" height="90" />
                <Typography className={classes.heading} variant="h2" align="center" fontStyle='normal'> Anna's Cuisine </Typography>
                <img className={classes.image} src={mcute} alt="mcute" height="90"  />
            </AppBar>
            {/* <Root>
                <Divider>CENTER</Divider>
            </Root> */}
            {/* <DividerLine 
                text = '分割线'
                margin = '30px'
                color = 'green'
                lineColor = 'green'
            /> */}
            {/* <Divider 
                text = '分割线'
                margin = '100px'
                color = 'green'
                lineColor = 'green'
            /> */}
            {/* <Root>
                shangmian
                <Divider>dd</Divider>
                xiamian
            </Root> */}
            <LinearProgress />
            <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
                <Tabs value={value} onChange={handleChange} aria-label="basic tabs example">
                    <Tab label="Item One" {...a11yProps(0)} />
                    <Tab label="Item Two" {...a11yProps(1)} />
                    <Tab label="Item Three" {...a11yProps(2)} />
                </Tabs>
            </Box>
            <Grow in>
                <Container>
                    <Grid className={classes.mainContainer} container justify="space-between" alignItems="stretch" spacing={3}>
                        <TabPanel value={value} index={0}>
                            <Grid item xs={12} sm={7}>
                                <Menu setCurrentId={setCurrentId} />
                            </Grid>
                            <Grid item xs={12} sm={4}>
                                {/* <Button variant="contained"  size="small" onClick={clear} fullWidth>Clear info</Button> */}
                                {/* <Button variant="contained"  size="small" fullWidth onClick={()=>{window.location.href="localhost:3000/getRevenue"}}>View Revenue</Button> */}
                                <Form currentId={currentId} setCurrentId={setCurrentId}/>
                            </Grid>
                        </TabPanel>
                        <TabPanel value={value} index={1}>
                            <Grid item xs={12} sm={4}>
                                {/* <Button variant="contained"  size="small" onClick={clear} fullWidth>Clear info</Button> */}
                                {/* <Button variant="contained"  size="small" fullWidth onClick={()=>{window.location.href="localhost:3000/getRevenue"}}>View Revenue</Button> */}
                                {/* <Revenue setCurrentIdRevenue={setCurrentIdRevenue}/> */}
                                <Revenue />
                            </Grid>
                        </TabPanel>
                    </Grid>
                </Container>
            </Grow>
        </Container>
    )
}

export default App;
